sinus._x = 75; sinus.lineStyle(3,0);
for (i = 0; i < Math.PI * 4; i += 0.01) 
{
	x = i*30;
	y = Math.sin(i) * 70;
	sinus.moveTo(x,y);
	sinus.lineTo(x + 1, y);
}

