_root.createEmptyMovieClip("spiral", 1);
spiral._y = 200;
spiral._x = 150; 
spiral.lineStyle(3,0);
px = 3; // сжатие по X
py = 3; // сжатие по Y
for (t = 0; t < Math.PI * 6; t += 0.01)
{
	x2 = (Math.cos(t) + t * Math.sin(t)) * px;
	y2 = (Math.sin(t) - t * Math.cos(t)) * py;
	x = Math.round(x2);
	y = Math.round(y2);
	spiral.moveTo(x,y);
	spiral.lineTo(x + 1, y);
}

//===================================================
//Улитка Паскаля
//===================================================
_root.createEmptyMovieClip("canvas", 1);
canvas._x = 150; 
canvas._y = 150;
canvas.lineStyle(3,0);
px = 60;
py = 60;
//for (t = 0; t < 65535; t += 0.0002)
for (t = 0; t < Math.PI * 6; t += 0.009)
{
	r = 1 - Math.cos(t * 10);
	x2 = (r * Math.cos(t * 10)) * px;
	y2 = (r * Math.sin(t * 10)) * py;
	x = Math.round(x2);
	y = Math.round(y2);
	canvas.moveTo(x,y);
	canvas.lineTo(x + 1, y);
}

//===================================================
//Эпициклоида
// 60 - радиус неподвижного круга
// 20 - радиус подвижного круга
//===================================================
//_root.createEmptyMovieClip("canvas", 1);
//canvas._y = 200;
//canvas._x = 150; 
//canvas.lineStyle(3,0);
r0 = 60;	// радиус неподвижного круга
r1 = 20;	// радиус скользящего круга
for (t = 0; t < Math.PI * 6; t += 0.02)
{
	x = (r0 + r1) * Math.cos(t) - r1 * Math.cos(((r0 + r1) / r1) * t);
	y = (r0 + r1) * Math.sin(t) - r1 * Math.sin(((r0 + r1) / r1) * t);
	x = Math.round(x);
	y = Math.round(y);
	x = x + 150;
	canvas.moveTo(x,y);
	canvas.lineTo(x+1,y);
}

