-- export drupal books pages --> WP page SELECT menu_links.mlid FROM menu_links WHERE menu_links.menu_name IN ( SELECT menu_name FROM menu_links WHERE link_title LIKE 'художники' AND module='book' ) ORDER BY weight ASC;
