-- export drupal gallery vocabulary --> WP category SELECT term_data.tid, term_data.name, term_data.description, term_data.weight, term_hierarchy.parent, url_alias.dst FROM term_data LEFT JOIN term_hierarchy ON term_hierarchy.tid=term_data.tid LEFT JOIN url_alias ON url_alias.src=CONCAT('taxonomy/term/',term_data.tid) WHERE vid='1';
