//--------------------------------------------------------------
// Получение названия статьи из ее текста
//--------------------------------------------------------------
function cut_name_st ($str1)
 {

  $pos_str = strpos($str1, " (");

  if ($pos_str <= 0)
    {
      $pos_str = strpos($str1, " -");
    }

  $str_res = substr($str1, 0, $pos_str);

  return $str_res;

 }

//--------------------------------------------------------------
// Проверка - первый символ полученного названия - заглавная русская буква ?
//--------------------------------------------------------------
function test_code($str1)
 {
//  echo "test_code\n";

  $test_code = ord($str1);
  if (($test_code < 192) || ($test_code > 223))
    {
      $res = "lower"; 
    }
  else
    $res = "upper"; 

  return $res;

 }
