function cancelEvent(e){
     try {
         if (e) {
             e.returnValue = "Вы точно хотите закрыть вкладку?";
             e.cancelBubble = true;
             if (e.stopPropagation)
                 e.stopPropagation();
             if (e.preventDefault)
                 e.preventDefault();
         }
     } catch (err) {}
     return "Вы точно хотите закрыть вкладку?";
}

