============================ скролл со смещением вверх
         $('a[href*=#]:not([href=#])').click(function(e) {
             e.preventDefault;
             var target = $(this.hash);
             target = target.length ? target : $('[name=' +
this.hash.slice(1) +']');
             if (target.length) {
                 $('html,body').animate({
                     scrollTop: target.offset().top -25
                 }, 1000);
             }
             return false;
         });

