<script>
//необходимо показать блок #scroll-block, начиная с 1 трети страницы и 
скрыть блок при достижении верхней границы блока #action-main (скролл вниз)
$(document).ready(
	function() 
	{
		$("#scroll-block").hide().css("top","100%");
console.log( "HTML-page height = " + $(document).height() );
console.log( "viewport height = " + $(window).height() );
console.log ("action offset left = " + $("#action-main").offset().left );
console.log ("action offset top = " + $("#action-main").offset().top );


var start_scroll_pos = $(document).height() / 3;
console.log ("start_scroll_pos = " + start_scroll_pos );

		var k1 = $("#scroll-block").height() / 2.5;
console.log ("k1 = " + k1 +", #scroll-block height" + $("#scroll-block").height() );
		
		var end_scroll_pos = $("#action-main").offset().top - ( ($(window).height() / 2)+k1 );
console.log ("end_scroll_pos = " + end_scroll_pos );


		$(window).scroll(function() {
var st = $(window).scrollTop();
document.title = st;
			if ( ($(this).scrollTop() > start_scroll_pos ) && 
					($(this).scrollTop() < end_scroll_pos) ) 
			{
				$("#scroll-block").addClass("sale-fixed").show().animate({ top: "30%", }, 1200);
			} 
			else 
			{
				//$("#scroll-block").css("top","100%");
				$("#scroll-block").removeClass("sale-fixed").hide();
			}

		});// end scroll

	}
);// end ready
</script>

