//-------------------------------------
function clear($filename)
{
    //$h=fopen($filename,"rw");
    //$string=fread($h,filesize($filename));
    //fclose ($h);

    $string=file_get_contents($filename);
    //echo "<pre>";
    //echo $string;
    //echo "</pre>";
    //echo "<br/>";

    echo "Filesize: ".filesize($filename);
    echo "<br/>";

    //Заменить кривые коды перехода строки на нормальные: 
    //для этого нужно только удалить "\r". 
    //Переходы бывают нормальными (но разными!): "\n" или "\r\n". Еще бывают глюки, типа "\r\r\n".
    $string=str_replace("\r","",$string);

    echo "string size: ".strlen($string);
    echo "<br/>";

    file_put_contents($filename,$string);
    //$filename='clear.cat_description.txt';
    //$h=fopen($filename,"w");
    //fwrite ($h,$string);
    //fclose($h);
    
    echo "Filesize: ".filesize($filename);
    echo "<br/>";
}//------------------- end function
?>

