$(document).ready(
     function()
     {
         var out = navigator.userAgent+"\n\r";
       var isiPhone = navigator.userAgent.toLowerCase().indexOf("iphone");
       var isiPad = navigator.userAgent.toLowerCase().indexOf("ipad");
       var isiPod = navigator.userAgent.toLowerCase().indexOf("ipod");
       var Chrome = navigator.userAgent.toLowerCase().indexOf("chrome");
       var Firefox = navigator.userAgent.toLowerCase().indexOf("firefox");
       if(isiPhone > -1)
       {
             out += "iPhone detect\n\r";
             $("body").addClass("iphone");
            var iHeight = window.screen.height;
            if(iHeight <= 480)
            {
               out += 'iPhone 2 or iPhone 3 or iPhone 3GS\n\r';
            }
            else if(iHeight > 480 && iHeight <=960)
            {
               out += 'iPhone 4\n\r';
            }
            else if(iHeight > 960)
            {
               out += 'iPhone 5\n\r';
            }
       }
       if(isiPad > -1)
       {
             out += "iPad detect\n\r";
       }
       if(isiPod > -1)
       {
             out = "iPod detect\n\r";
       }

       if( Chrome > -1)
       {
             out += "Chrome detect\n\r";
             $("body").addClass("chrome");
       }
       if( Firefox > -1)
       {
             out += "Firefox detect\n\r";
             $("body").addClass("firefox");
       }
/*
      var res = "window.screen = " + window.screen.width + "x" +
window.screen.height + ", ";
     res += "window.inner = " + window.innerWidth + "x" +
window.innerWidth + ", ";
     res += "window.screen.avail = " + window.screen.availWidth + "x" +
window.screen.availHeight + "";
     out += res;
alert ( out)    ;
*/

         //$("#scroll-block").css("top","100%");
         $("#scroll-block").css("top","700px");

