         $("#scroll-block").hide();

         if ( $("#action-main").length > 0 )
         {
             var start_scroll_pos = $(document).height() / 3;
             var k1 = $("#scroll-block").height() / 2.5;
             var end_scroll_pos = $("#action-main").offset().top - (
($(window).height() / 2)+k1 );
         }
}//--------- end test iphone

         $(window).scroll(function() {
             if ( $("#action-main").length > 0 )
             {
if( isiPhone == -1)
{
                 if ( ( $(this).scrollTop() > start_scroll_pos ) &&
                         ( $(this).scrollTop() < end_scroll_pos ) )
                 {
                     //вычисление левой позиции блока для центрирования
на странице
                     calc_left_pos();
//$("#scroll-block").addClass("sale-fixed").show().animate({ top: "30%",
}, 600);
$("#scroll-block").addClass("sale-fixed").show().animate({ top: "250px",
}, 600);
                 }
                 else
                 {
$("#scroll-block").removeClass("sale-fixed").hide();
                 }
}//--------- end test iphone
             }
         });//------------- end scroll

         $( window ).resize(function() {
             calc_left_pos();
         });

         function calc_left_pos()
         {
             //вычисление левой позиции блока для центрирования на странице
             var w_center = $(window).width() / 2;
             var left_margin = $("#scroll-block").width() / 2;
             var left_pos = w_center - left_margin;

             $("#scroll-block").css("left", left_pos + "px");
         }//------------- end func

     }
