// function 11
//===================================================
px = 60;
py = 60;
for (t = 0; t &lt; Math.PI * 9; t += 0.007)
{
	r = 1 - Math.cos(t * 10);
	x2 = (r * Math.cos(t * 7)) * px;
	y2 = (r * Math.sin(t * 7)) * py;
	x = Math.round(x2);
	y = Math.round(y2);
	x = x + 800;
	canvas.moveTo(x,y);
	canvas.lineTo(x + 1, y);
}

//===================================================
// function 12
//===================================================
px = 60;
py = 60;
for (t = 0; t &lt; Math.PI; t += 0.002)
{
	r = 1 - Math.cos(t * 10);
	x2 = (r * Math.cos(t * 21)) * px;
	y2 = (r * Math.sin(t * 21)) * py;
	x = Math.round(x2);
	y = Math.round(y2);
	x = x + 1050;
	canvas.moveTo(x,y);
	canvas.lineTo(x + 1, y);
}

