//Конструктор и объекты на основе этого конструктора

//создание конструктора
var MyClass = function(w, h)
{
  this.width = w;
  this.height = h;

  this.result = function()
  {
    console.log (&quot;Метод конструктора. Square value is &quot; + ( this.width * this.height));
  }
  //в функции-конструкторе не должно быть return объект!!!!!!
}

//объявление метода в прототипе конструктора
// (необходимо, чтобы не дублировать общий метод square_result в каждом экземпляре конструтора)
MyClass.prototype.proto_func = function()
{
    console.log ( &quot;Метод прототипа. Площадь равна &quot; + ( this.width * this.height ) );
}

var a = new MyClass (10, 10);
var b = new MyClass (20, 20);

a.result();
b.proto_func();

console.log( a.constructor );
console.log( MyClass.prototype );
