//вывод иконок аппл на запрос /apple-touch-icon-precomposed.png
     public function actionGetIcons()
     {
         if ( Yii::app()-&gt;theme-&gt;name == &quot;fromvanessa&quot; )
         {
             $url = &quot;/themes/&quot; . Yii::app()-&gt;theme-&gt;name. &quot;/assets/image&quot;;
         }
         if ( Yii::app()-&gt;theme-&gt;name == &quot;idinfoproduct&quot; )
         {
             $url = &quot;/themes/&quot; . Yii::app()-&gt;theme-&gt;name. &quot;/assets/img&quot;;
         }

         if( isset($url) )
         {
             switch( Yii::app()-&gt;request-&gt;requestUri )
             {
                 case &quot;/apple-touch-icon-precomposed.png&quot;:
                         $url = $url . &quot;/apple-touch-icon.png&quot;;
                     break;

                 case &quot;/apple-touch-icon.png&quot;:
                         $url = $url . &quot;/apple-touch-icon.png&quot;;
                     break;

                 case &quot;/browserconfig.xml&quot;:
                         $url = &quot;/themes/&quot; . Yii::app()-&gt;theme-&gt;name.
&quot;/assets&quot;;
                         $url = $url . &quot;/browserconfig.xml&quot;;
                     break;
             }
             $this-&gt;redirect( $url );
         }

     }

