====================================================== backup.cgi
#!/bin/sh
echo &quot;Content-type: text/html&quot;
echo &quot;&quot;
echo &quot;&lt;html&gt;&quot;
echo &quot;&lt;head&gt;&quot;
echo &quot;&lt;meta charset='utf-8'&gt;&quot;
echo &quot;&lt;/head&gt;&quot;
echo &quot;&lt;body&gt;&quot;

#****************************
# WPUT, закачать архив на сервер
#****************************
function upload_file
{
	server=&quot;ftp.it-works.16mb.com&quot;
	user=&quot;u131428543&quot;
	pwd=&quot;w0rdpasss&quot;
	upload_path=&quot;/public_html/0_backup&quot;

	dir=$1
	local_file=$2
	remote_file=$2

	echo &quot;&lt;h2&gt;WPUT, upload archive $dir/$local_file on $server&lt;/h2&gt;&quot;
	wput --timestamping --verbose $dir/$local_file ftp://$user:$pwd@$server/$upload_path/$remote_file


	local_file=$3
	remote_file=$3

	echo &quot;&lt;h2&gt;WPUT, upload archive $dir/$local_file on $server&lt;/h2&gt;&quot;
	wput --reupload --timestamping --verbose $dir/$local_file ftp://$user:$pwd@$server/$upload_path/$remote_file
}
#---------------------- end func

#**************
# BACKUP SITE 
#**************
function backup_site
{
	db_name=$1
	site_name=$2
#------------------------------------- DB
	echo &quot;&lt;h2&gt;backup database $db_name&lt;/h2&gt;&quot;
	echo &quot;&lt;pre&gt;&quot;
	mv ${backup_dir}/${db_name}.sql.gz ${backup_dir}/1.${db_name}.sql.gz
	mysqldump --user=${user_db} --password=${password} --database ${db_name} | gzip &gt; &quot;${backup_dir}/${db_name}.sql.gz&quot;
	cp ${backup_dir}/${db_name}.sql.gz $location_arc2

#------------------------------------- FILES
	echo &quot;&lt;h2&gt;backup $site_name files&lt;/h2&gt;&quot;
	mv ${backup_dir}/${site_name}_files.zip ${backup_dir}/1.${site_name}_files.zip
	cd $location_sites
	zip -r ${backup_dir}/${site_name}_files.zip ${site_name}
	cp ${backup_dir}/${site_name}_files.zip $location_arc2
	echo &quot;&lt;/pre&gt;&quot;

#------------------------------------- WPUT
	#upload_file ${backup_dir} ${site_name}_files.zip ${db_name}.sql.gz
}
#---------------------- end func


