{
	$.cookie(&quot;feedback_username&quot;, $(&quot;#feedback-username&quot;).val(), { expires: 7});
	$.cookie(&quot;feedback_email&quot;, $(&quot;#feedback-email&quot;).val(), { expires: 7});
