function change_video(url)
{
	var ua = navigator.userAgent.toLowerCase();
//alert(ua);
	// Gecko = Mozilla + Firefox + Netscape
	if (ua.indexOf(&quot;gecko&quot;) != -1) 
	{
		var browser =&quot;gecko&quot;;
		var video_frame = document.getElementById('video_frame');
		video_frame.src = url;
		//alert (video_frame.outerHTML);
	}
	else
	{
		var video_frame = window.frames['video_frame'];	
		video_frame.location.replace(url);
	}
}

