-- export drupal photogallery_images nodes --&gt; WP post -- получить ноды с прикрепленным изображением SELECT node.nid, node.title, node.status, node.created, node.changed, term_node.tid, -- term_data.tid, term_data.vid, term_data.name as category_name, term_url_alias.dst as category_nicename, url_alias.dst as node_nicename, node_revisions.teaser, node_revisions.body, content_type_photogallery_image.nid, content_type_photogallery_image.field_author_value, content_type_photogallery_image.field_create_date_value, -- content_type_photogallery_image.field_style_value, -- content_type_photogallery_image.field_genre_value, -- content_type_photogallery_image.field_technique_value, content_type_photogallery_image.field_title_value, content_type_photogallery_image.field_preview_img_value, content_type_photogallery_image.field_big_img_value, content_type_photogallery_image.field_original_img_value, content_type_photogallery_image.field_zoom_img_value, content_type_photogallery_image.field_img1_gallery_fid, content_type_photogallery_image.field_info_value, files.fid, files.filepath, content_field_filename.field_filename_value FROM node LEFT JOIN node_revisions ON node_revisions.nid=node.nid LEFT JOIN content_type_photogallery_image ON content_type_photogallery_image.nid=node.nid LEFT JOIN files ON files.fid=content_type_photogallery_image.field_img1_gallery_fid LEFT JOIN term_node ON term_node.nid=node.nid LEFT JOIN term_data ON term_data.tid=term_node.tid LEFT JOIN content_field_filename ON content_field_filename.nid=node.nid LEFT JOIN url_alias ON url_alias.src=CONCAT('node/',node.nid) LEFT JOIN url_alias as term_url_alias ON term_url_alias.src=CONCAT('taxonomy/term/',term_node.tid) WHERE node.type='photogallery_images' AND term_data.vid='1' ORDER BY term_data.tid;
