-- export drupal books pages --&gt; WP page SELECT menu_links.mlid FROM menu_links WHERE menu_links.menu_name IN ( SELECT menu_name FROM menu_links WHERE link_title LIKE 'художники' AND module='book' ) ORDER BY weight ASC;
-----------------------------------------
-- export drupal gallery vocabulary --&gt; WP category SELECT term_data.tid, term_data.name, term_data.description, term_data.weight, term_hierarchy.parent, url_alias.dst FROM term_data LEFT JOIN term_hierarchy ON term_hierarchy.tid=term_data.tid LEFT JOIN url_alias ON url_alias.src=CONCAT('taxonomy/term/',term_data.tid) WHERE vid='1';
