{
	if (document.getElementById(nnodeid).style.display == \&quot;none\&quot;)
	  {
		document.getElementById(nnodeid).style.display = \&quot;\&quot;
	  }
	else
	 {
		document.getElementById(nnodeid).style.display = \&quot;none\&quot;
	 }
}
