#================================= вирт. хост на алиасе сетевой карты 1.ifconfig eth0:1 192.168.0.161 up 2.echo ifconfig eth0:1 192.168.0.161 up &gt; /etc/rc.local 3.mcedit /etc/hosts 192.168.0.161 zelenyidom.vm
4.mcedit /etc/apache2/sites-available/default ........ &lt;VirtualHost zelenyidom.vm:80&gt; # ServerAdmin webmaster@localhost DocumentRoot /var/www/sites/zelenyidom # &lt;Directory /&gt; # Options FollowSymLinks # AllowOverride All # &lt;/Directory&gt; &lt;Directory /var/www/sites/zelenyidom&gt; Options Indexes FollowSymLinks MultiViews AllowOverride All Order allow,deny Allow from all &lt;/Directory&gt;
ErrorLog /var/log/apache2/error_zelenyidom.log
# Possible values include: debug, info, notice, warn, error, crit, # alert, emerg. LogLevel warn
CustomLog /var/log/apache2/access_zelenyidom.log combined &lt;/VirtualHost&gt; ............. 5./etc/init.d/apache2 restart
6.telnet zelenyidom.vm 80 GET /
7.cat /var/log/apache2/access_zelenyidom.log 192.168.0.161 - - [07/Oct/2012:10:13:49 +0700] &quot;GET /&quot; 200 1181 &quot;-&quot; &quot;-&quot; ---------------------------------------------------
#====== вирт. хост без отдельного адреса, контент виден при запросе из браузера &lt;VirtualHost *:80&gt; DocumentRoot /var/www/sites/vhost2 ServerName vhost2.vm ServerAlias vhost2.vm &lt;Directory /var/www/sites/vhost2&gt; Options Indexes FollowSymLinks MultiViews AllowOverride All Order allow,deny Allow from all &lt;/Directory&gt;
ErrorLog /var/log/apache2/error_vhost2.log LogLevel warn CustomLog /var/log/apache2/access_vhost2.log combined &lt;/VirtualHost&gt;
========================================= виртуальные хосты для подготовки статической копии сайта smarty_site
cat /etc/hosts 127.0.0.2 smarty_site_static 127.0.0.3 smarty_site
httpd.conf NameVirtualHost 127.0.0.2 &lt;VirtualHost 127.0.0.2&gt; DocumentRoot /mnt/disk2/documents/0_sites/smarty_site_static ServerName smarty_site_static ErrorLog /mnt/disk2/temp/smarty_site_static_error_log CustomLog /mnt/disk2/temp/smarty_site_static_access_log common &lt;/VirtualHost&gt;
NameVirtualHost 127.0.0.3 &lt;VirtualHost 127.0.0.3&gt; DocumentRoot /mnt/transcend/0_sites/smarty_site ServerName smarty_site ErrorLog /mnt/disk2/temp/smarty_site_error_log CustomLog /mnt/disk2/temp/smarty_site_access_log common &lt;/VirtualHost&gt; =========================================

