function view_child_termins($terms, $test_termin_tid)
{
	global $base_url;
	for ($n2=0;$n2&lt;count($terms);$n2++)
	{
		if ($terms[$n2]-&gt;parents[0] == $test_termin_tid)
		{
			echo &quot;&lt;li&gt;&quot;;
			echo &quot;	&lt;a href='&quot;.$base_url.&quot;/?q=taxonomy/term/&quot;.$terms[$n2]-&gt;tid.&quot;'&gt;&quot;.$terms[$n2]-&gt;name.&quot;&lt;/a&gt;&quot;;
			echo &quot;	&lt;ul&gt;&quot;;
			view_child_termins($terms, $terms[$n2]-&gt;tid);
			echo &quot;	&lt;/ul&gt;&quot;;
			echo &quot;&lt;/li&gt;&quot;;
		}
	}
}//----------------- end func
