function phptemplate_preprocess_page(&amp;$vars) {

//echo &quot;function TOP_light_phptemplate_preprocess_page&quot;;

     if (isset($vars['node'])) {
     if($vars['node']-&gt;type == 'story')
     {
         $vars['template_files'][] = 'page-'.$vars['node']-&gt;type;
     }
     }

