//пример конструктора
function Point(x, y){
  this.x = x;
  this.y = y;
}

var topLeft = new Point(0, 0);
var topRight = new Point(100, 0);
var bottomLeft = new Point(0, 100);
var bottomRight = new Point(100, 100);

console.log("topLeft x = " + topLeft.x);
console.log("topRight x = " + topRight.x);
console.log("bottomLeft x = " + bottomLeft.x);
console.log("bottomRight x = " + bottomRight.x);

//замыкание
(function(){
	var ZList = ZList || function(options){

		//обязательный элемент здесь будет автоматически хранится версия
		// из version control
		var _repositoryRevision = "$Rev$"; 

		// private variables and functions
		var _init = function(){
		};

		var _build = function(target){
			return target + " is building!";
		};

		// public interfaces
		return{
			revision:_repositoryRevision,
			build:	function(target){ 
				return _build(target); 
			}
		};
	};
	window.ZList = ZList;	
})();
---------------
использование в main.js
	var a = ZList("some options");
console.log("a.revision = " + a.revision);

	var result_build = a.build("table1");
console.log("result_build = " + result_build);
