#!/usr/bin/python
# -*- coding: utf-8 -*-
print "Content-type: text/html"
print ""
print "<html>"
print "<body>"
print "test XML"
print "<br>"

print "xml.parsers"
print "<br>"
from xml.parsers import expat

def start_element(name, attrs):
    print "start_element", name, attrs,"<br>"

def end_element(name):
    print "end_element", name,"<br>"

def char_data(data):
    print "char_data", repr(data),"<br>"

p = expat.ParserCreate()
p.returns_unicode = 0
p.StartElementHandler = start_element
p.EndElementHandler = end_element
p.CharacterDataHandler = char_data
p.Parse ("<?xml version=\"1.0\"?> <parent id=\"top\">    <child1 name=\"paul привет\">Text goes here</child1>    <child2 name=\"fred\">More text</child2> </parent>")

print "<hr>"
print "xml.dom.minidom"
print "<br>"
