<?php

// УСТАНАВЛИВАЕТ СЕТЕВОЕ СОЕДИНЕНИЕ С БАЗОЙ ДАННЫХ MYSQL
 $db = mysql_connect ("localhost", "Roman", "******")
        or die ("Соединение не установлено! \n");
 print ("Соединение установлено! \n");

 print ("<br>");


// ВЫБОР ОДНОЙ БАЗЫ ДАННЫХ MYSQL.

  mysql_select_db("my_db") or die("Could not select database");

// ПОСЫЛАЕТ ЗАПРОС БАЗЕ ДАННЫХ MYSQL.

 $query = "SELECT * FROM one_table";
 $result = mysql_query($query) or die("Query failed");

/* Printing results in HTML */

  print "<table>\n";
  while ($line = mysql_fetch_array($result, MYSQL_ASSOC))
   {
      print "\t<tr>\n";

      foreach ($line as $col_value) {
          print "\t\t<td>$col_value</td>\n";
      }
      print "\t</tr>\n";
   }
  print "</table>\n";

  /* Free resultset */
  mysql_free_result($result);


 mysql_close($db);
?> 

