&lt;?
//echo &quot;arg(0) == &quot;.arg(0);
if (arg(0) == 'node')
{
	$node=node_load(arg(1));
//echo &quot;&lt;pre&gt;&quot;;
//print_r($node);
//print_r($node-&gt;field_preview_img);
//print_r($node-&gt;field_url);
//print_r($node-&gt;field_subfolder);
//print_r($node-&gt;field_filename);
//echo &quot;&lt;/pre&gt;&quot;;

	$subfolder=$node-&gt;field_subfolder['und'][0]['value'];

	for ($n1=0; $n1 &lt; count($node-&gt;field_filename['und']); $n1++)
	{

//------------- формирование ссылки из поля field_filename (filename#описание)
		$file=explode(&quot;#&quot;,$node-&gt;field_filename['und'][$n1]['value']);
//echo &quot;file = &lt;pre&gt;&quot;;
//print_r($file);
//echo &quot;&lt;/pre&gt;&quot;;
		if (!empty($file[1]))
		{
			$link_text=$file[1];
		}
		else
		{
			$link_text=$file[0];
		}

echo &quot;&lt;b&gt;&quot;.$link_text.&quot;&lt;/b&gt;&quot;;
echo &quot;&lt;ul&gt;&quot;;

		$pos = strpos($link_text, &quot;external&quot;);
//----- если ссылка локальная, то сформировать ссылки из поля field_url
		if ($pos === false)
		{
			for ($n2=0; $n2&lt;count($node-&gt;field_url['und']); $n2++)
			{
				$site = $node-&gt;field_url['und'][$n2]['value'];
				$url = explode(&quot;#&quot;,$site);
//echo &quot;url = &lt;pre&gt;&quot;;
//print_r($url);
//echo &quot;&lt;/pre&gt;&quot;;
				if (!empty($url[1]))
				{
					$url_text=$url[1];
				}
				else
				{
					$url_text=$url[0];
				}

echo &quot;&lt;li&gt;&quot;;
echo &quot;&lt;a href='&quot;.$url[0].&quot;/&quot;.$subfolder.&quot;/&quot;.$file[0].
&quot;' target=_blank&gt;url &quot;.$url_text.&quot;&lt;/a&gt;&quot;;
echo &quot;&lt;/li&gt;&quot;;
			}//-------------------------------------- end for
		}
		else
		{
//----- добавить к ссылке имя сайта
			echo &quot;&lt;p&gt;&quot;;
			echo &quot;&lt;a href='&quot;.$file[0].&quot;' target='_blank'&gt;&quot;.$link_text.&quot;&lt;/a&gt;&quot;;
			echo &quot;&lt;/p&gt;&quot;;
		}
echo &quot;&lt;/ul&gt;&quot;;

	}//----------------------------- end for

}//------------------------- end if
?&gt;

