//echo &quot;arg(0) == &quot;.arg(0);
if (arg(0) == 'node')
{
	echo &quot;&lt;h2&gt;local url&lt;/h2&gt;&quot;;
	$node=node_load(arg(1));
//echo &quot;&lt;pre&gt;&quot;;
//print_r($node);
//print_r($node-&gt;field_preview_img);
//print_r($node-&gt;field_url);
//print_r($node-&gt;field_subfolder);
//print_r($node-&gt;field_filename);
//echo &quot;&lt;/pre&gt;&quot;;

	echo &quot;&lt;li&gt;&lt;a href='file:///mnt/terra/&quot;.
	$node-&gt;field_subfolder['und'][0]['value'].&quot;/&quot;.
	$node-&gt;field_filename['und'][0]['value'].
&quot;' target=_blank&gt;linux url&lt;/a&gt;&lt;/li&gt;&quot;;

	echo &quot;&lt;li&gt;&lt;a href='file:///m:/&quot;.
	$node-&gt;field_subfolder['und'][0]['value'].&quot;/&quot;.
	$node-&gt;field_filename['und'][0]['value']
.&quot;' target=_blank&gt;windows url&lt;/a&gt;&lt;/li&gt;&quot;;
}
?&gt;

