src=&quot;file:///mnt/terra/&lt;? echo $node-&gt;field_subfolder['und'][0]['value'].&quot;/&quot;.$node-&gt;field_filename['und'][0]['value'] ?&gt;&quot;  
width=&quot;800&quot; height=&quot;600&quot; 
ShowControls=&quot;1&quot; 
ShowStatusBar=&quot;0&quot; 
ShowDisplay=&quot;0&quot; 
autostart=&quot;0&quot;&gt;
