&lt;br&gt;============================= FLV&lt;br&gt;
&lt;?php
if (arg(0) == 'node')
{
	$site = &quot;http://localhost&quot;;
	//$site = &quot;file:///mnt/terra&quot;;
	//$site = &quot;file://m:&quot;;
	$subfolder = $node-&gt;field_subfolder['und'][0]['value'];
	$file = $node-&gt;field_filename['und'][0]['value'];
	$videofile = $site.&quot;/&quot;.$subfolder.&quot;/&quot;.$file;
	global $base_url;
echo &quot;videofile = &quot;.$videofile;
echo &quot;&lt;br&gt;&quot;;
echo &quot;base_url = &quot;.$base_url;
echo &quot;&lt;br&gt;&quot;;

echo &quot;&lt;div align='center'&gt;&quot;;
echo &quot;&lt;object 
type='application/x-shockwave-flash' 
data='&quot;.$base_url.&quot;/sites/all/players/uflvplayer_500x375.swf' 
height='640' width='480'&gt;
	&lt;param name='bgcolor' value='#FFFFFF' /&gt;
	&lt;param name='allowFullScreen' value='true' /&gt;
	&lt;param name='allowScriptAccess' value='always' /&gt;
	&lt;param name='movie' value='&quot;.$base_url.&quot;/sites/all/players/uflvplayer_500x375.swf' /&gt;
&lt;param name='FlashVars' 
value='way=&quot;.$videofile.&quot;&amp;amp;swf=&quot;.$base_url.&quot;/sites/all/players/uflvplayer_500x375.swf&amp;amp;w=400&amp;amp;h=340&amp;amp;pic=&amp;amp;autoplay=0&amp;amp;tools=1&amp;amp;skin=greyblack&amp;amp;volume=70&amp;amp;q=&amp;amp;comment=\' /&gt;
&lt;/object&gt;&quot;;

echo &quot;&lt;/div&gt;&quot;;
}
?&gt;

