if (arg(0) == 'node')
{
	//$site = &quot;http://localhost&quot;;
	$site = &quot;file:///mnt/terra&quot;;
	//$site = &quot;file://m:&quot;;
	$subfolder = $node-&gt;field_subfolder['und'][0]['value'];
	$file = $node-&gt;field_filename['und'][0]['value'];
	$videofile = $site.&quot;/&quot;.$subfolder.&quot;/&quot;.$file;
	global $base_url;
echo &quot;videofile = &quot;.$videofile;
echo &quot;&lt;br&gt;&quot;;
echo &quot;base_url = &quot;.$base_url;
echo &quot;&lt;br&gt;&quot;;

echo &quot;&lt;div align='center'&gt;&quot;;
echo &quot;
&lt;object data='&quot;.$videofile.&quot;' type='video/mpeg' width='640' height='480'&gt;
	&lt;param name='src' value='&quot;.$videofile.&quot;'/&gt;
	&lt;param name='autoplay' value='false'/&gt;
	&lt;param name='autoStart' value='0'/&gt;
&lt;/object&gt;&quot;;

echo &quot;&lt;/div&gt;&quot;;
}
?&gt;

&lt;br&gt;======================JW Player&lt;br&gt;
&lt;?php
if (arg(0) == 'node')
{
	$site = &quot;http://localhost&quot;;
	//$site = &quot;file:///mnt/terra&quot;;
	//$site = &quot;file://m:&quot;;
	$subfolder = $node-&gt;field_subfolder['und'][0]['value'];
	$file = $node-&gt;field_filename['und'][0]['value'];
	$videofile = $site.&quot;/&quot;.$subfolder.&quot;/&quot;.$file;
	global $base_url;
echo &quot;videofile = &quot;.$videofile;
echo &quot;&lt;br&gt;&quot;;
echo &quot;base_url = &quot;.$base_url;
echo &quot;&lt;br&gt;&quot;;

	echo &quot;&lt;div id='mediaplayer'&gt;JW Player goes here&lt;/div&gt;&quot;;
	echo &quot;&lt;script type='text/javascript' src='&quot;.$base_url.&quot;/sites/all/players/JWPlayer/jwplayer.js'&gt;&lt;/script&gt;&quot;;
	echo &quot;&lt;script type='text/javascript'&gt;
	jwplayer('mediaplayer').setup({
		flashplayer: '&quot;.$base_url.&quot;/sites/all/players/JWPlayer/player.swf',
		file: '&quot;.$videofile.&quot;',
width: 800,
height: 600,
		image: ''
	});
&lt;/script&gt;&quot;;
}
?&gt;

